mapper.select:
SELECT * FROM ( SELECT ROWNUM rn, A .ASNREFERENCE1 order_no, A .ASNSTATUS inbound_status, A .ASNTYPE inbound_type, A .ADDTIME inbound_time, b.linestatus, b.sku, b.expectedqty, b.receivedtime, b.receivedqty, c.fmsku putaway_sku, c.fmqty putaway_qty, c.addtime putaway_time FROM doc_asn_header A INNER JOIN DOC_ASN_DETAILS b ON A .ASNNO = b.ASNNO AND A.ASNREFERENCE1 = #{param.orderNo} AND b.sku = #{param.sku}
AND TO_CHAR (A.addtime, 'YYYY-MM-DD') BETWEEN #{param.fromDate} AND #{param.toDate}
INNER JOIN ACT_Transaction_Log c ON c.docno = A .ASNNO AND b.sku = c.fmsku AND c.transactionType = 'PA' WHERE ROWNUM <= #{page.limit} ) d WHERE d.rn > #{page.offset}

SELECT
COUNT(1)
FROM
doc_asn_header A
INNER JOIN DOC_ASN_DETAILS b ON A .ASNNO = b.ASNNO
AND A.ASNREFERENCE1 = #{param.orderNo} 
AND b.sku = #{param.sku} 

    AND TO_CHAR (A.addtime, 'YYYY-MM-DD') BETWEEN #{param.fromDate}
    AND #{param.toDate}

INNER JOIN ACT_Transaction_Log c ON c.docno = A .ASNNO AND b.sku = c.fmsku
AND c.transactionType = 'PA'
