mapper.select:
SELECT b.* FROM ( SELECT ROWNUM AS rn, A .* FROM ( SELECT T .soreference1 oms_order, TO_CHAR ( T .addtime, 'yyyy-MM-dd hh:mm' ) order_issue_time, TO_CHAR ( MAX (D .pickedtime), 'yyyy-MM-dd hh:mm' ) order_picking_time, TO_CHAR ( MAX (D .checktime), 'yyyy-MM-dd hh:mm' ) order_check_time, TO_CHAR ( MAX (D .shipmenttime), 'yyyy-MM-dd hh:mm' ) order_loading_time, DECODE ( T .sostatus, '00', '创建订单', '30', '部分分配', '40', '分配完成', '50', '部分捡货', '60', '捡货完成', '62', '部分装箱', '63', '完全装箱', '65', '部分装车', '66', '装车完成', '70', '部分发运', '80', '完全发运', '90', '订单取消', '99', '订单完成' ) wms_status FROM act_allocation_details D RIGHT JOIN doc_order_header T ON D .orderno = T .orderno LEFT JOIN doc_loading_details l ON T .orderno = l.orderno LEFT JOIN doc_loading_header H ON l.ldlno = H .ldlno LEFT JOIN t_order_info o ON o.orderno = T .soreference1 WHERE TO_CHAR (T .addtime, 'YYYY-MM-DD') BETWEEN #{param.fromDate} AND #{param.toDate}
AND T .soreference1 = #{param.omsOrder}
GROUP BY T .orderno, T .soreference1, T .userdefine1, T .addtime, T .sostatus, D .pickedtime, D .checktime, D .shipmenttime, T .channel, H .carrierid, H .driver, H .loadingtotime, o.status, o.edittime ORDER BY T .addtime ) A WHERE ROWNUM <= #{page.limit} ) b WHERE b.rn > #{page.offset}

SELECT
  count( 1)
FROM
 doc_order_header
 where  to_char(addtime,'YYYY-MM-DD') between #{param.fromDate} and #{param.toDate}

    AND soreference1 = #{param.omsOrder}
