mapper.select:
select d.pickedwho as user_id, count(distinct d.orderno) as orderNum, count(sku) as skuNum from ACT_Allocation_Details d where 1 = 1
and to_char(d.pickedtime,'yyyy-mm-dd') >= #{start_date}
and to_char(d.pickedtime,'yyyy-mm-dd') <= #{end_date}
and pickedwho = #{username}
group by d.pickedwho order by count(sku) desc

select d.checkwho as user_id, count(distinct d.orderno) as num from ACT_Allocation_Details d where 1 = 1
and to_char(d.checktime,'yyyy-mm-dd') >= #{start_date}
and to_char(d.checktime,'yyyy-mm-dd') <= #{end_date}
and checkwho = #{username}
group by d.checkwho order by count(distinct d.orderno) desc

select d.shipmentwho as user_id, count(distinct d.orderno) as num from ACT_Allocation_Details d where 1 = 1
and to_char(d.shipmenttime,'yyyy-mm-dd') >= #{start_date}
and to_char(d.shipmenttime,'yyyy-mm-dd') <= #{end_date}
and shipmentwho = #{username}
group by d.shipmentwho order by d.shipmentwho

SELECT * FROM (SELECT tt.*, ROWNUM AS rowno FROM ( SELECT a.sku as sku, substr(b.descr_e,0,70) as sku_desc, a.locationid, a.traceid, SUM(A.QTY ) as qty from inv_lot_loc_id a LEFT JOIN BAS_SKU B ON A.SKU = B.SKU and b.customerid=a.customerid LEFT JOIN INV_LOT_ATT D ON d.lotnum=a.lotnum LEFT JOIN VIEW_MULTIWAREHOUSE C ON A.LOCATIONID = C.LOCATIONID LEFT JOIN BAS_LOCATION E ON E.LOCATIONID=A.LOCATIONID WHERE
D.LOTATT08 = 'N' AND E.PICKZONE <>'KE010301' AND A.LOCATIONID NOT IN ('STAGEKE01','SORTATIONKE01')
and a.LOCATIONID LIKE CONCAT( CONCAT('KE01-01-',#{shelf}),'%')
and a.LOCATIONID LIKE CONCAT( CONCAT('%',#{location}),'%') group BY a.sku,b.descr_e,a.locationid, a.traceid order by a.locationid ,a.traceid
) tt WHERE ROWNUM <= #{end} ) table_alias WHERE table_alias.rowno >= #{offset}
